mapper.select:
SELECT
-- 级联表的字段 ,zhuhu.zhuhu_danhao_number as zhuhuDanhaoNumber ,zhuhu.zhuhu_name as zhuhuName ,zhuhu.zhuhu_phone as zhuhuPhone ,zhuhu.zhuhu_id_number as zhuhuIdNumber ,zhuhu.zhuhu_address as zhuhuAddress ,zhuhu.zhuhu_photo as zhuhuPhoto ,zhuhu.zhuhu_age as zhuhuAge ,zhuhu.zhuhu_yimiao_types as zhuhuYimiaoTypes ,zhuhu.zhuhu_laiyuandi as zhuhuLaiyuandi ,zhuhu.zhuhu_hesuan_types as zhuhuHesuanTypes ,zhuhu.zhuhu_xingcheng as zhuhuXingcheng ,zhuhu.zhuhu_content as zhuhuContent ,zhuhu.zhuhu_types as zhuhuTypes ,zhuhu.zhuhu_time as zhuhuTime ,zhuhu.insert_time as insertTime
FROM dengji a left JOIN zhuhu zhuhu ON a.zhuhu_id = zhuhu.id
and a.id in
#{item}
and a.zhuhu_id = #{params.zhuhuId}
and a.dengji_types = #{params.dengjiTypes}
and a.tiwen >= #{params.tiwenStart}
and a.tiwen <= #{params.tiwenEnd}
and a.lai_address like CONCAT('%',#{params.laiAddress},'%')
and a.qu_address like CONCAT('%',#{params.quAddress},'%')
and a.dengji_content like CONCAT('%',#{params.dengjiContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and zhuhu.zhuhu_danhao_number like CONCAT('%',#{params.zhuhuDanhaoNumber},'%')
and zhuhu.zhuhu_name like CONCAT('%',#{params.zhuhuName},'%')
and zhuhu.zhuhu_phone like CONCAT('%',#{params.zhuhuPhone},'%')
and zhuhu.zhuhu_id_number like CONCAT('%',#{params.zhuhuIdNumber},'%')
and zhuhu.zhuhu_address like CONCAT('%',#{params.zhuhuAddress},'%')
and zhuhu.zhuhu_age >= #{params.zhuhuAgeStart}
and zhuhu.zhuhu_age <= #{params.zhuhuAgeEnd}
and zhuhu.zhuhu_yimiao_types = #{params.zhuhuYimiaoTypes}
and zhuhu.zhuhu_laiyuandi like CONCAT('%',#{params.zhuhuLaiyuandi},'%')
and zhuhu.zhuhu_hesuan_types = #{params.zhuhuHesuanTypes}
and zhuhu.zhuhu_xingcheng like CONCAT('%',#{params.zhuhuXingcheng},'%')
and zhuhu.zhuhu_content like CONCAT('%',#{params.zhuhuContent},'%')
and zhuhu.zhuhu_types = #{params.zhuhuTypes}
and UNIX_TIMESTAMP(zhuhu.zhuhu_time) >= UNIX_TIMESTAMP(#{params.zhuhuTimeStart})
and UNIX_TIMESTAMP(zhuhu.zhuhu_time) <= UNIX_TIMESTAMP(#{params.zhuhuTimeEnd})
and UNIX_TIMESTAMP(zhuhu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(zhuhu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
order by a.${params.orderBy} desc
